export default {
  name: 'GrainPyramid',
  data() {
    return {
      chart: null,
      currentYear: '2024',
      years: ['2024', '2023', '2022', '2021'],
      pyramidData: {
        '2024': [{
          name: '谷物',
          value: 61.59
        }, {
          name: '豆类',
          value: 9.62
        }, {
          name: '折粮薯类',
          value: 28.80
        }],
        '2023': [{
          name: '谷物',
          value: 61.67
        }, {
          name: '豆类',
          value: 9.58
        }, {
          name: '折粮薯类',
          value: 28.75
        }],
        '2022': [{
          name: '谷物',
          value: 61.91
        }, {
          name: '豆类',
          value: 9.36
        }, {
          name: '折粮薯类',
          value: 28.73
        }],
        '2021': [{
          name: '谷物',
          value: 62.37
        }, {
          name: '豆类',
          value: 8.65
        }, {
          name: '折粮薯类',
          value: 28.98
        }]
      }
    };
  },
  mounted() {
    this.initChart();
    window.addEventListener('resize', this.handleResize);
    if (window.ResizeObserver) {
      this.observer = new ResizeObserver(() => {
        this.handleResize();
      });
      this.observer.observe(this.$refs.chart);
    }
  },
  beforeDestroy() {
    window.removeEventListener('resize', this.handleResize);
    if (this.observer) {
      this.observer.disconnect();
    }
    if (this.chart) {
      this.chart.dispose();
    }
  },
  watch: {
    currentYear() {
      this.updateChart();
    }
  },
  methods: {
    initChart() {
      this.chart = this.$echarts.init(this.$refs.chart);
      this.updateChart();
    },
    updateChart() {
      // 获取当前年份的数据
      const data = this.pyramidData[this.currentYear];
      const option = {
        title: {
          text: `${this.currentYear}年粮食作物面积占比`,
          textStyle: {
            color: '#fff',
            fontSize: 14
          },
          left: 'center',
          top: 5
        },
        tooltip: {
          trigger: 'item',
          formatter: '{b}: {c}%',
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          borderColor: 'rgba(255, 255, 255, 0.2)',
          textStyle: {
            color: '#fff'
          }
        },
        series: [{
          name: '面积占比',
          type: 'funnel',
          width: '80%',
          height: '80%',
          left: '10%',
          top: '15%',
          minSize: '0%',
          maxSize: '100%',
          sort: 'none',
          gap: 2,
          label: {
            show: true,
            position: 'inside',
            formatter: '{b} {c}%',
            fontSize: 14,
            fontWeight: 'bold',
            color: '#fff'
          },
          itemStyle: {
            borderColor: 'rgba(255, 255, 255, 0.5)',
            borderWidth: 1
          },
          emphasis: {
            label: {
              fontSize: 16
            },
            itemStyle: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          },
          data: data
        }],
        color: ['#f9c74f', '#90be6d', '#43aa8b']
      };
      this.chart.setOption(option, true);
    },
    handleResize() {
      if (this.chart) {
        this.chart.resize();

        // 响应式调整
        const containerWidth = this.$refs.chart.clientWidth;
        if (containerWidth < 300) {
          this.chart.setOption({
            series: [{
              width: '90%',
              left: '5%',
              label: {
                fontSize: 12
              }
            }]
          });
        } else {
          this.chart.setOption({
            series: [{
              width: '80%',
              left: '10%',
              label: {
                fontSize: 14
              }
            }]
          });
        }
      }
    }
  }
};
