  watch: {
    activeTabIndex() {
      this.updateChart();
    }
  },
  mounted() {
    this.$nextTick(() => {
      this.initChart();
    });
    window.addEventListener('resize', this.handleResize);
    if (window.ResizeObserver) {
      this.observer = new ResizeObserver(() => {
        this.handleResize();
      });
      this.observer.observe(this.$el);
    }
  },
  beforeDestroy() {
    window.removeEventListener('resize', this.handleResize);
    if (this.observer) {
      this.observer.disconnect();
    }
    if (this.chart) {
      this.chart.dispose();
      this.chart = null;
    }
  },
  methods: {
    initChart() {
      this.chart = this.$echarts.init(this.$refs.chart);
      this.updateChart();
    },
    updateChart() {
      if (!this.chart) return;
      const activeTab = this.tabs[this.activeTabIndex];
      const data = this.consumptionData[activeTab.label];

      // 对数据进行排序（从小到大）
      const sortedData = [...data].sort((a, b) => a.value - b.value);

      // 将数据转换为适合条形图的格式
      const villages = sortedData.map(item => item.name);
      const values = sortedData.map(item => item.value);

      // 设置固定高度为600px，确保可以容纳所有村庄数据
      this.$refs.chart.style.height = '600px';

      // 获取容器宽度决定是否显示完整村名
      const containerWidth = this.$el.clientWidth;
      const showFullName = containerWidth > 350;
      const option = {
        tooltip: {
          trigger: 'axis',
          formatter: `{b}<br/>${activeTab.label}: {c} ${activeTab.unit}`,
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          borderColor: 'rgba(255, 255, 255, 0.2)',
          textStyle: {
            color: '#fff'
          }
        },
        grid: {
          top: '3%',
          right: showFullName ? '8%' : '5%',
          bottom: '3%',
          left: showFullName ? '15%' : '20%',
          containLabel: true
        },
        xAxis: {
          type: 'value',
          name: activeTab.unit,
          nameTextStyle: {
            color: 'rgba(255, 255, 255, 0.7)'
          },
          axisLine: {
            lineStyle: {
              color: 'rgba(255, 255, 255, 0.3)'
            }
          },
          axisLabel: {
            color: 'rgba(255, 255, 255, 0.7)',
            fontSize: containerWidth < 400 ? 10 : 11
          },
          splitLine: {
            lineStyle: {
              color: 'rgba(255, 255, 255, 0.1)'
            }
          }
        },
        yAxis: {
          type: 'category',
          data: villages,
          inverse: false,
          axisLine: {
            lineStyle: {
              color: 'rgba(255, 255, 255, 0.3)'
            }
          },
          axisLabel: {
            color: 'rgba(255, 255, 255, 0.7)',
            margin: 10,
            fontSize: containerWidth < 400 ? 10 : 11,
            formatter: function (value) {
              if (!showFullName && value.length > 3) {
                return value.substring(0, 2) + '..';
              }
              return value;
            }
          }
        },
        series: [{
          name: activeTab.label,
          type: 'bar',
          data: values,
          barWidth: '60%',
          itemStyle: {
            color: this.getColorByTabIndex(this.activeTabIndex)
          },
          label: {
            show: containerWidth > 300,
            position: 'right',
            color: '#fff',
            fontSize: containerWidth < 400 ? 9 : 10,
            formatter: `{c} ${activeTab.unit}`
          }
        }]
      };
      this.chart.setOption(option, true);
    },
    getColorByTabIndex(index) {
      const colors = [new this.$echarts.graphic.LinearGradient(0, 0, 1, 0, [{
        offset: 0,
        color: 'rgba(249, 199, 79, 0.3)'
      }, {
        offset: 1,
        color: 'rgba(249, 199, 79, 0.8)'
      }]), new this.$echarts.graphic.LinearGradient(0, 0, 1, 0, [{
        offset: 0,
        color: 'rgba(144, 190, 109, 0.3)'
      }, {
        offset: 1,
        color: 'rgba(144, 190, 109, 0.8)'
      }]), new this.$echarts.graphic.LinearGradient(0, 0, 1, 0, [{
        offset: 0,
        color: 'rgba(67, 170, 139, 0.3)'
      }, {
        offset: 1,
        color: 'rgba(67, 170, 139, 0.8)'
      }])];
      return colors[index] || colors[0];
    },
    handleResize() {
      if (this.chart) {
        this.chart.dispose();
        this.initChart(); // 重新初始化以应用响应式变化
      }
    }
