import * as echarts from 'echarts';
export default {
  name: 'GrainPyramid',
  data() {
    return {
      currentYear: '2024',
      years: ['2024', '2023', '2022', '2021'],
      dataMap: {
        '2024': {
          grain: 61.59,
          beans: 9.62,
          potato: 28.80
        },
        '2023': {
          grain: 61.67,
          beans: 9.58,
          potato: 28.75
        },
        '2022': {
          grain: 61.91,
          beans: 9.36,
          potato: 28.73
        },
        '2021': {
          grain: 62.37,
          beans: 8.65,
          potato: 28.98
        }
      },
      chart: null
    };
  },
  mounted() {
    this.initChart();
    window.addEventListener('resize', this.resizeChart);
  },
  beforeUnmount() {
    window.removeEventListener('resize', this.resizeChart);
    if (this.chart) {
      this.chart.dispose();
      this.chart = null;
    }
  },
  methods: {
    initChart() {
      this.chart = echarts.init(this.$refs.chartRef);
      this.updateChart();
    },
    changeYear(year) {
      this.currentYear = year;
      this.updateChart();
    },
    updateChart() {
      if (!this.chart) return;
      const data = [{
        name: '谷物',
        value: this.dataMap[this.currentYear].grain,
        num: this.dataMap[this.currentYear].grain.toFixed(2),
        ratio: this.dataMap[this.currentYear].grain.toFixed(2) + '%',
        itemStyle: {
          color: '#f9c74f'
        }
      }, {
        name: '薯类',
        value: this.dataMap[this.currentYear].potato,
        num: this.dataMap[this.currentYear].potato.toFixed(2),
        ratio: this.dataMap[this.currentYear].potato.toFixed(2) + '%',
        itemStyle: {
          color: '#4cd5ce'
        }
      }, {
        name: '豆类',
        value: this.dataMap[this.currentYear].beans,
        num: this.dataMap[this.currentYear].beans.toFixed(2),
        ratio: this.dataMap[this.currentYear].beans.toFixed(2) + '%',
        itemStyle: {
          color: '#90be6d'
        }
      }];
      const option = {
        title: {
          text: this.currentYear + '年粮食作物占比',
          left: 'center',
          top: 10,
          textStyle: {
            color: '#fff',
            fontSize: 16
          }
        },
        tooltip: {
          trigger: 'item',
          formatter: function (params) {
            return `${params.marker}${params.name}：${params.data.num}%`;
          }
        },
        legend: {
          data: ['谷物', '薯类', '豆类'],
          orient: 'vertical',
          left: '5%',
          top: 'center',
          textStyle: {
            color: '#fff'
          },
          itemStyle: {
            borderWidth: 0
          }
        },
        series: [{
          name: '占比',
          type: 'funnel',
          left: '25%',
          top: 60,
          bottom: 30,
          width: '70%',
          min: 0,
          max: 100,
          minSize: '0%',
          maxSize: '100%',
          sort: 'ascending',
          gap: 2,
          label: {
            formatter: function (params) {
              return `{b|${params.data.num}%}\n{c|占比${params.data.ratio}}`;
            },
            rich: {
              b: {
                color: '#fff',
                lineHeight: 30,
                fontWeight: "bold",
                fontSize: 16
              },
              c: {
                color: 'rgba(255,255,255,0.7)',
                lineHeight: 20,
                fontSize: 12,
                padding: [0, 0]
              }
            }
          },
          labelLine: {
            show: false
          },
          itemStyle: {
            borderColor: "rgba(255,255,255,0.5)",
            borderWidth: 1
          },
          emphasis: {
            label: {
              fontSize: 18
            }
          },
          data: data
        }, {
          name: '占比',
          type: 'funnel',
          left: '25%',
          top: 60,
          bottom: 30,
          width: '70%',
          min: 0,
          max: 100,
          minSize: '0%',
          maxSize: '100%',
          sort: 'ascending',
          gap: 2,
          label: {
            position: 'inside',
            formatter: '{b}',
            fontSize: 14,
            color: '#fff'
          },
          itemStyle: {
            opacity: 1,
            borderColor: '#fff',
            borderWidth: 1
          },
          emphasis: {
            label: {
              fontSize: 16
            }
          },
          data: data,
          z: 100
        }]
      };
      this.chart.setOption(option);
    },
    resizeChart() {
      if (this.chart) {
        this.chart.resize();
      }
    }
  }
};
